import {take} from './take';

/**
 * Same as {@link take}.
 * @function head
 */
export const head = take;

export * from './StopIteration';
export * from './_count';
export * from './_next';
export * from './_range';
export * from './by';
export * from './consume';
export * from './count';
export * from './cycle';
export * from './drop';
export * from './exhaust';
export * from './first';
export * from './frame';
export * from './head';
export * from './iter';
export * from './len';
export * from './last';
export * from './list';
export * from './ncycle';
export * from './next';
export * from './pick';
export * from './range';
export * from './tail';
export * from './take';
export * from './tee';
export * from './trunc';

/**
 * Returns the iterator for the input iterable.
 *
 * @param {Iterable} iterable - The input iterable.
 * @returns {Iterator}
 *
 */
export function iter(iterable) {
	return iterable[Symbol.iterator]();
}

import {next} from './next';
import {tail} from './tail';

/**
 * Returns the last value of the input iterable. If the iterable is
 * exhausted, throws a {@link StopIteration}.
 *
 * @param {Iterable} iterable - The input iterable.
 * @returns {Object} The last value of the input iterable.
 */
export function last(iterable) {
	return next(tail(iterable, 1));
}

/**
 * Returns the length of the input array.
 *
 * @example
 * // returns 7
 * len( list( range( 7 ) ) ) ;
 *
 * @param {Array} array - The input array.
 * @returns {Number}
 *
 */
export function len(array) {
	return array.length;
}

/**
 * Returns an array containing all elements of the input iterable.
 *
 * @example
 * // return [0,1,2]
 * list( range( 3 ) ) ;
 *
 * @param {Iterable} iterable - The input iterable.
 * @returns {Array}
 *
 */
export function list(iterable) {
	return Array.from(iterable);
}

/**
 * Same as {@link cycle} but only cycles a limited number of times.
 *
 * @example
 * // returns [0,1,0,1,0,1,0,1]
 * list(ncycle(range(2),4)) ;
 *
 * @param {Iterable} iterable - The input iterable.
 * @param {Number} n - The number of times to cycle through the input iterable.
 * @returns {Iterator}
 *
 */
export function* ncycle(iterable, n) {
	const buffer = [];

	for (const item of iterable) {
		yield item;
		buffer.push(item);
	}

	if (buffer.length === 0) {
		return;
	}

	while (--n > 0) {
		yield* buffer;
	}
}

import {StopIteration} from './StopIteration';

/**
 * Returns the next value of the input iterator. If the iterator is exhausted,
 * throws a {@link StopIteration}, unless a <code>dflt</code> argument has been
 * specified, in which case the <code>dflt</code> argument is returned.
 *
 * @param {Iterator} iterator - The input iterator.
 * @param {Object} [dflt=undefined] - The default value to return in the case
 * that the input iterator is exhausted.
 * @returns {Object} The next value of the input iterator.
 */
export function next(iterator, dflt = undefined) {
	const x = iterator.next();

	if (x.done) {
		if (dflt === undefined) {
			throw new StopIteration();
		} else {
			return dflt;
		}
	}

	return x.value;
}

/**
 * Yields some of the properties of the input object. The properties to yield
 * are designated by the input iterable.
 *
 * @example
 * // returns [ 'a' , 'e' , 'c' ]
 * list( pick( 'abcde' , [ 0 , 4 , 2 ] ) ) ;
 *
 * @param {Object} object - The input object.
 * @param {Iterable} iterable - The input iterable.
 * @returns {Iterator}
 */
export function* pick(object, iterable) {
	for (const key of iterable) {
		yield object[key];
	}
}

import {_range} from './_range';

/**
 * Yields values in a range, separated by a fixed constant called step. If this
 * step is negative, the range has to be given in reverse order, that is,
 * largest value first, smallest value second. Both the starting value and the
 * step value are optional. By default the starting value is <code>0</code>.
 * The default for the step value is <code>1</code>.
 *
 * @param {Number} [start=0] - The starting value.
 * @param {Number} stop - The stopping value.
 * @param {Number} [step=1] - The step value.
 * @returns {Iterator}
 */
export function range(start, stop, step) {
	if (stop === undefined) {
		return _range(0, start, 1);
	}

	if (step === undefined) {
		return _range(start, stop, 1);
	}

	return _range(start, stop, step);
}

import deque from '@aureooms/js-collections-deque';

import {drop} from './drop';

/**
 * Returns the last <code>n</code> values of the input iterable in an array.
 * If <code>n</code> is negative, behaves like <code>{@link drop}( iterable,
 * -n)</code>.
 *
 * @example
 * // returns [ 3 , 4 ]
 * list( tail( range( 5 ) , 2 ) ) ;
 *
 * @param {Iterable} iterable - The input iterable.
 * @param {Number} n - The number of values to include in the output.
 * @returns {Iterator} - The last <code>n</code> values of the input iterable.
 */
export function* tail(iterable, n) {
	if (n < 0) {
		yield* drop(iterable, -n);
		return;
	}

	yield* deque(iterable, n);
}

import {iter} from './iter';
import {trunc} from './trunc';

/**
 * Yields the first <code>n</code> elements of the input iterable. If
 * <code>n</code> is negative, behaves like <code>{@link trunc}( iterable,
 * -n)</code>.
 *
 * @example
 * // returns [ 0 , 1 , 2 ]
 * list( take( range( 5 ) , 3 ) ) ;
 *
 * @param {Iterable} iterable - The input iterable.
 * @param {Number} n - The number of elements to include in the output.
 * @returns {Iterator} - The first <code>n</code> elements of the input iterable.
 */
export function* take(iterable, n) {
	if (n < 0) {
		yield* trunc(iterable, -n);
		return;
	}

	const iterator = iter(iterable);

	while (n-- > 0) {
		const current = iterator.next();

		if (current.done) {
			return;
		}

		yield current.value;
	}
}

import {iter} from './iter';
import {list} from './list';
import {map} from '../map/map';

import deque from '@aureooms/js-collections-deque';

/**
 * Returns <code>n</code> copies of the input iterable. Note that if the input
 * iterable is an iterator, then it must be discarded by the caller after
 * calling tee.
 *
 * @param {Iterable} iterable - The input iterable.
 * @param {Number} n - The number of copies to make.
 * @returns {Iterator[]}
 */
export function tee(iterable, n) {
	const iterator = iter(iterable);

	const copies = [];

	while (n-- > 0) {
		copies.push(deque());
	}

	const gen = function* (mycopy) {
		while (true) {
			if (mycopy.length === 0) {
				const current = iterator.next();

				if (current.done) {
					return;
				}

				for (const copy of copies) {
					copy.append(current.value);
				}
			}

			yield mycopy.popleft();
		}
	};

	return list(map(gen, copies));
}

import deque from '@aureooms/js-collections-deque';

import {iter} from './iter';
import {take} from './take';

/**
 * Yields all elements of the iterable except the last <code>n</code> ones. If
 * <code>n</code> is negative, behaves like <code>{@link take}( iterable, -n
 * )</code>.
 *
 * @example
 * // returns [ 0 , 1 , 2 ]
 * list( trunc( range( 5 ) , 2 ) ) ;
 *
 * @param {Iterable} iterable - The input iterable.
 * @param {Number} n - The number of elements to exclude from the output.
 * @returns {Iterator}
 */
export function* trunc(iterable, n) {
	if (n < 0) {
		yield* take(iterable, -n);
		return;
	}

	if (n === 0) {
		yield* iterable;
		return;
	}

	const iterator = iter(iterable);

	const buffer = deque(null, n);

	while (n-- > 0) {
		const event = iterator.next();
		if (event.done) {
			return;
		}

		buffer.append(event.value);
	}

	for (const value of iterator) {
		yield buffer.popleft();
		buffer.append(value);
	}
}

