/**
 * Computes the closure of a unary operator, starting from a single element.
 *
 * @experimental Should handle d-ary operators in the future. Not sure the name
 * is well chosen. An actual closure function would keep track of encountered
 * elements to stop when all elements have been found.
 *
 * @example
 * closure( x => x + 1 , 0 ) ; // 0 1 2 3 ...
 *
 * @example
 * closure( x => x * 2 , 1 ) ; // 1 2 4 8 ...
 *
 * @param {Function} operator The operator.
 * @param {Object} start The starting element.
 * @returns {Iterator} Iterator over the closure.
 */
export function* closure(operator, start) {
	yield start;

	let element = start;

	while (true) {
		element = operator(element);

		yield element;
	}
}

import deque from '@aureooms/js-collections-deque';

import {iter, _next, count} from '../base';

/**
 * Computes the product of two iterables in a way that allows for one or both
 * of them to be infinite (in constrast with {@link product}).
 * Although the output iterator may be infinite, it is guaranteed that each
 * value of the product is located at some finite position in the output
 * iterator.
 *
 * If one of the two inputs has finite size N, it is guaranteed that memory
 * usage never exceeds O(N) values. If both inputs have infinite size, then
 * memory usage grows proportionately to the square root of the number of
 * output pairs.
 *
 * @example
 * // returns [ [ 0 , 0 ] , [ 0 , 1 ] , [ 1 , 0 ] , [ 1 , 1 ] ]
 * list( diagonal( range( 2 ) , range( 2 ) ) ) ;
 *
 * @param {Iterable} A - The first iterable.
 * @param {Iterable} B - The second iterable.
 * @returns {Iterator}
 *
 */
export function* diagonal(A, B) {
	const itA = iter(A);
	const itB = iter(B);
	const _A = deque();
	const _B = deque();

	for (const n of count()) {
		let _a = _next(itA);
		let _b = _next(itB);
		if (!_a.done) {
			// eslint-disable-next-line no-negated-condition
			if (!_b.done) {
				_A.append(_a.value);
				_B.append(_b.value);
				for (let i = 0; i <= n; ++i) yield [_A.get(i), _B.get(n - i)];
			} else {
				if (_B.length === 0) return;
				do {
					_A.append(_a.value);
					_A.popleft();
					for (let i = 0; i < n; ++i) yield [_A.get(i), _B.get(n - i - 1)];
					_a = _next(itA);
				} while (!_a.done);

				break;
			}
			// eslint-disable-next-line no-negated-condition
		} else if (!_b.done) {
			if (_A.length === 0) return;
			do {
				_B.append(_b.value);
				_B.popleft();
				for (let i = 0; i < n; ++i) yield [_A.get(i), _B.get(n - i - 1)];
				_b = _next(itB);
			} while (!_b.done);

			break;
		} else {
			break;
		}
	}

	// Yield lower triangle

	const n = _A.length;

	for (let i = 1; i < n; ++i) {
		for (let j = i; j < n; ++j) {
			yield [_A.get(j), _B.get(n - j + i - 1)];
		}
	}
}

export * from './closure';
export * from './diagonal';
export * from './nth';

import deque from '@aureooms/js-collections-deque';
import {next, drop, StopIteration} from '../base';

/**
 * Returns the <code>n</code>th value of the input iterable (<code>n</code>
 * is a 0-based index). If
 * <code>n</code> is negative, returns the <code>n</code>th value from the
 * end.
 * If no such value exists, throws a {@link StopIteration}.
 *
 * @example
 * // returns 3
 * nth( range( 5 ) , 3 ) ;
 *
 * @param {Iterable} iterable - The input iterable.
 * @param {Number} n - The index of the value to output.
 * @returns {Object} The <code>n</code>th value of the input iterable.
 */
export function nth(iterable, n) {
	if (n < 0) {
		const deq = deque(iterable, -n);
		if (deq.len() < -n) throw new StopIteration();
		return deq.get(0);
	}

	return next(drop(iterable, n));
}

export * from './base';
export * from './extra';
export * from './map';
export * from './reduce';

/**
 * Algorithm used by {@link product} to compute the product of one or more
 * iterables from pools of symbols.
 *
 * @example
 * // Ax Ay Bx By Cx Cy Dx Dy
 * _product(['xy', 'ABCD'], 0 , 2) ;
 *
 * @example
 * // 000 001 010 011 100 101 110 111
 * _product([[0,1],[0,1],[0,1]], 0 , 3) ;
 *
 * @param {Array[]} pools - The pools of symbols in reverse order.
 * @param {Number} i - Index of the pool to draw the next symbol from
 * @param {Number} n - Number of pools in total.
 * @returns {Iterator}
 */
export function* _product(pools, i, n) {
	if (i === n) {
		yield [];
		return;
	}

	const iterable = pools[i];

	for (const buffer of _product(pools, i + 1, n)) {
		for (const item of iterable) {
			buffer.push(item);

			yield buffer;

			buffer.pop(item);
		}
	}
}

import {list, map, iter, _next} from '..';

/**
 * Zips iterables together. Yields a tuple containing the first element of each
 * iterable, then a tupe containing the second element of each iterable, etc.
 * Stops when one of the iterables runs out of elements.
 *
 * @example
 * // returns [ [ 'a' , 1 ] , [ 'b' , 2 ] , [ 'c' , 3 ] ]
 * list( _zip( [ 'abcd' , range(3) ] ) ) ;
 *
 * @param {Iterable[]} iterables - The iterables to zip.
 * @returns {Iterator}
 *
 */
export function* _zip(iterables) {
	const iterators = list(map(iter, iterables));

	if (iterators.length === 0) {
		return;
	}

	while (true) {
		const buffer = [];

		for (const result of map(_next, iterators)) {
			if (result.done) {
				return;
			}

			buffer.push(result.value);
		}

		yield buffer;
	}
}

import {iter} from '..';

/**
 * Zips exactly two iterables together. Yields a tuple containing the first
 * element of each iterable, then a tupe containing the second element of each
 * iterable, etc.  Stops when one of the two iterables runs out of elements.
 *
 * @example
 * // returns [ [ 'a' , 1 ] , [ 'b' , 2 ] , [ 'c' , 3 ] ]
 * list( _zip2( 'abcd' , range(3) ) ) ;
 *
 * @param {Iterable} A - The first iterable.
 * @param {Iterable} B - The second iterable.
 * @returns {Iterator}
 *
 */
export function* _zip2(A, B) {
	A = iter(A);
	B = iter(B);

	while (true) {
		const a = A.next();
		if (a.done) {
			return;
		}

		const b = B.next();
		if (b.done) {
			return;
		}

		yield [a.value, b.value];
	}
}

import {list, map, iter, _next, repeat, enumerate} from '..';

/**
 * Same as _zip, but continues to yield zipped tuples until the last iterable is
 * exhausted.
 *
 * @example
 * // returns [['A','x'],['B','y'],['C','-'],['D','-']]
 * list( _ziplongest( '-' , [ 'ABCD', 'xy' ] ) ) ;
 *
 * @param fillvalue - The value to yield for iterators that are exhausted.
 * @param {Iterable[]} iterables - The iterables to zip.
 * @returns {Iterator}
 *
 */
export function* _ziplongest(fillvalue, iterables) {
	let counter = iterables.length;

	if (counter === 0) {
		return;
	}

	const filler = repeat(fillvalue);

	const iterators = list(map(iter, iterables));

	while (true) {
		const buffer = [];

		for (const [i, event] of enumerate(map(_next, iterators))) {
			if (event.done) {
				--counter;
				if (counter === 0) {
					return;
				}

				iterators[i] = filler;
				buffer.push(fillvalue);
			} else {
				buffer.push(event.value);
			}
		}

		yield buffer;
	}
}

/**
 * Chains input iterables one after the other. Returns an iterator that yields
 * all values of the first input iterable, then all values of the second, etc.
 *
 * @example
 * // returns [ 0 , 1 , 0 , 1 , 2 ]
 * list( chain( [ range( 2 ) , range( 3 ) ] ) ) ;
 *
 * @param {Iterable[]} iterables - The input iterables to chain.
 * @returns {Iterator}
 *
 */
export function* chain(iterables) {
	for (const iterable of iterables) {
		yield* iterable;
	}
}

import {list, range, pick} from '..';

/**
 * Yields all combinations of each possible choice of <code>r</code> elements
 * of the input iterable.
 *
 * @example
 * // AB AC AD BC BD CD
 * combinations('ABCD', 2)
 *
 * @example
 * // 012 013 023 123
 * combinations(range(4), 3)
 *
